    def is_used_before(self, object_id1, object_id2):
        object_id1 = make_list(object_id1)
        object_id2 = make_list(object_id2)
        final_entities1 = [trans for original_object, transformation_list in self.lists_created['transformations'].items() for trans in transformation_list if original_object in object_id1 and trans in self.lists_created['entities_created_that_remain']]
        final_entities2 = [trans for original_object, transformation_list in self.lists_created['transformations'].items() for trans in transformation_list if original_object in object_id2 and trans in self.lists_created['entities_created_that_remain']]
        final_entities1_uniq = set(final_entities1) - set(final_entities2)
        final_entities2_uniq = set(final_entities2) - set(final_entities1)
        if len(final_entities1_uniq) == 0 or len(final_entities2_uniq) == 0: return False
        steps_1 = [self.lists_created['entities_created'][entity] for entity in final_entities1_uniq]
        steps_2 = [self.lists_created['entities_created'][entity] for entity in final_entities2_uniq]
        return max(steps_1) < max(steps_2)

    def is_served_before(self, object_id1, object_id2):
        object_id1 = make_list(object_id1)
        object_id2 = make_list(object_id2)
        serve_idx1 = [self.lists_created['serving_order'].index(o) for o in object_id1 if o in self.lists_created['serving_order']]
        serve_idx2 = [self.lists_created['serving_order'].index(o) for o in object_id2 if o in self.lists_created['serving_order']]
        if len(serve_idx1) == 0 or len(serve_idx2) == 0: return False
        return min(serve_idx1) < min(serve_idx2)

    def are_objects_mixed_in_order(self, *args):
        arg_lists = [make_list(arg) for arg in args]
        for chosen_set in self.lists_created['mixed_object_sets_unique']:
            if all([any([object_id in chosen_set for object_id in object_category]) for object_category in arg_lists]):
                indices = [min([chosen_set.index(object_id) for object_id in object_category if object_id in chosen_set]) for object_category in args]
                if indices == sorted(indices):
                    return True
        return False

    def is_created(self, item):
        if item.endswith('s'): item = item[:-1]
        item = item.replace('_',' ')
        for entity_created in self.lists_created['entities_created']:
            entity_created = entity_created.replace('_',' ')
            if item in entity_created:
                return True
        return False

    def is_added_while_preparing(self, ingredient, entity_list):
        entity_list = make_list(entity_list)
        ingredient = make_list(ingredient)
        for entity in entity_list:
            entity = entity.replace('_',' ')
            exact_entity = None
            for entity_created in self.lists_created['entities_created']:
                if entity in entity_created.replace('_',' '):
                    exact_entity = entity_created
            if exact_entity is None:
                continue
            for original_object, transformation_list in self.lists_created['transformations'].items():
                if exact_entity in transformation_list and original_object in ingredient:
                    return True
        return False

    ## Check if any of the entities created are in the list of locations
    def all_final_food_location(self, locations):
        for entity_created in self.lists_created['entities_created']:
            if self.get_location(entity_created) is None: continue
            next_entity = entity_created
            for recursion in range(10):
                if recursion >= 9: 
                    # print(f"WARNING: Recursion depth exceeded when checking location of {entity_created}!!!")
                    break
                next_entity = self.get_location(next_entity)
                if next_entity in locations:
                    return True
                if next_entity == 'home':
                    pass
        return False

    def any_final_food_location(self, locations):
        for entity_created in self.lists_created['entities_created']:
            next_entity = entity_created
            for recursion in range(10):
                if recursion >= 9: 
                    # print(f"WARNING: Recursion depth exceeded when checking location of {entity_created}!!!")
                    break
                next_entity = self.get_location(next_entity)
                if next_entity in locations:
                    return True
                if next_entity == 'home':
                    break
        return False
    
    def is_added_to_ingredient_or_while_preparing(self, list_of_ingredients, list_of_creations, list_of_topping_lists):
        something_used = False
        if not any([self.is_available(x) for x in list_of_topping_lists]):
            return 'inapplicable'
        for ingredients, creation in zip(list_of_ingredients, list_of_creations):
            if self.is_used(ingredients) or self.is_created(creation):
                something_used = True
                for topping_list in list_of_topping_lists:
                    if not self.are_objects_mixed(topping_list, ingredients) or self.is_added_while_preparing(topping_list, creation):
                        return 'violated'
        if something_used:
            return 'satisfied'
        else:
            return 'inapplicable'
